import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import "./login.css";

const Login = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSwitch = () => setIsLogin(!isLogin);

  const handleSubmit = (e) => {
    e.preventDefault();
    // login or signup logic here
    alert(`${isLogin ? "Logging in" : "Signing up"} with ${email}`);
  };

  return (
    <div className="login-wrapper">
      <div className="form-section">
        <div className="form-box">
          <div className="tab-header">
            <span
              className={isLogin ? "active" : ""}
              onClick={() => setIsLogin(true)}
            >
              Login
            </span>
            <span
              className={!isLogin ? "active" : ""}
              onClick={() => setIsLogin(false)}
            >
              Sign up
            </span>
          </div>

          <Form onSubmit={handleSubmit}>
            <Form.Group>
              <Form.Control
                type="email"
                placeholder="Email or phone number"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group>
              <Form.Control
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            {isLogin && (
              <div className="forgot-link">Forgot your password?</div>
            )}

            <Button type="submit" className="submit-btn">
              {isLogin ? "Login" : "Sign up"}
            </Button>
          </Form>
        </div>
      </div>

      <div className="image-section">
        <img
          src={`${process.env.PUBLIC_URL}/images/logooo.png`}
          alt="Illustration"
        />
      </div>
    </div>
  );
};

export default Login;
