import React, { useState } from "react";
import "/Contact.css";

const Contact = () => {
  const [userData, setUserData] = useState({
    firstName: "",
    lastName: "",
    phone: "",
    email: "",
    address: "",
    message: "",
  });

  const postUserData = (event) => {
    const { name, value } = event.target;
    setUserData({ ...userData, [name]: value });
  };

  const submitData = async (event) => {
    event.preventDefault();
    const { firstName, lastName, phone, email, address, message } = userData;

    if (firstName && lastName && phone && email && address && message) {
      const res = await fetch(
        "https://demo1-55b1e-default-rtdb.firebaseio.com/userDataRecords.json",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            firstName,
            lastName,
            phone,
            email,
            address,
            message,
          }),
        }
      );

      if (res.ok) {
        setUserData({
          firstName: "",
          lastName: "",
          phone: "",
          email: "",
          address: "",
          message: "",
        });
        alert("Data Stored");
      } else {
        alert("Submission failed");
      }
    } else {
      alert("Please fill in all fields");
    }
  };

  return (
    <div className="contact-container">
      <div className="contact-left">
        <h2>Contact Our Team</h2>
        <p>For any help or suggestions, feel free to contact us.</p>
        <img src="/images/callcenter.jpg" alt="Contact Illustration" className="contact-image" />
      </div>

      <div className="contact-right">
        <form onSubmit={submitData} className="contact-form">
          <div className="form-row">
            <input
              type="text"
              name="firstName"
              placeholder="First Name"
              value={userData.firstName}
              onChange={postUserData}
              required
            />
            <input
              type="text"
              name="lastName"
              placeholder="Last Name"
              value={userData.lastName}
              onChange={postUserData}
              required
            />
          </div>
          <div className="form-row">
            <input
              type="text"
              name="phone"
              placeholder="Phone Number"
              value={userData.phone}
              onChange={postUserData}
              required
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              value={userData.email}
              onChange={postUserData}
              required
            />
          </div>
          <input
            type="text"
            name="address"
            placeholder="Address"
            value={userData.address}
            onChange={postUserData}
            required
          />
          <textarea
            name="message"
            rows="4"
            placeholder="Your Message"
            value={userData.message}
            onChange={postUserData}
            required
          />
          <div className="checkbox">
            <input type="checkbox" id="agreement" />
            <label htmlFor="agreement">
              I agree, you may contact me via email or phone.
            </label>
          </div>
          <button type="submit" className="submit-button">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default Contact;
