import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Form, Alert, Button } from "react-bootstrap";
import GoogleButton from "react-google-button";
import { useUserAuth } from "../context/UserAuthContext";

import "../styles/Login.css";
import illustration from "../assets/login-illustration.png";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const { logIn, googleSignIn } = useUserAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    try {
      await logIn(email, password);
      navigate("/home");
    } catch (err) {
      setError(err.message);
    }
  };

  const handleGoogleSignIn = async (e) => {
    e.preventDefault();
    try {
      await googleSignIn();
      navigate("/home");
    } catch (error) {
      console.log(error.message);
    }
  };
  return(
<div className="login-page">
  <div className="login-left">
    <div className="navbar">
      <div className="logo">
        <img src={`${process.env.PUBLIC_URL}/images/logooo.png`} alt="Logo" height="40" />
      </div>
      <nav>
        <a href="/">Home</a>
        <a href="/">Our Products</a>
        <a href="/">About Us</a>
        <a href="/">Contact Us</a>
      </nav>
      <button className="signup-btn">ورود</button>
    </div>

    <div className="form-container">
      <div className="tab">
        <span className="active">Login</span>
        <span>Sign up</span>
      </div>
      <Form onSubmit={handleSubmit}>
        <Form.Group>
          <Form.Control
            type="email"
            placeholder="Email or phone number"
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group>
          <Form.Control
            type="password"
            placeholder="Password"
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <div className="forgot-password">Forgot your password?</div>
        <Button type="submit" className="custom-login-btn">Login</Button>
      </Form>
    </div>
  </div>

  <div className="login-right">
    <img src={`${process.env.PUBLIC_URL}/images/mainHero.png`} alt="Illustration" />
  </div>
</div>

);
};

export default Login;
